INSERT INTO `pokemon` (`ID`, `NAME`, `HP`, `ATTACK`, `DEFENSE`, `SPECIAL_ATTACK`, `SPECIAL_DEFENSE`, `SPEED`, `SPECIES`, `DESCRIPTION`, `HEIGHT`, `WEIGHT`, `SPRITE`, `THUMBNAIL`, `HIRES`, `REGION`) VALUES
(1, 'Bulbasaur', 45, 49, 49, 65, 65, 45, 'Seed Pokémon', 'Bulbasaur can be seen napping in bright sunlight. There is a seed on its back. By soaking up the sun’s rays, the seed grows progressively larger.', '0.7 m', '6.9 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/001.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/001.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/001.png', 1),
(2, 'Ivysaur', 60, 62, 63, 80, 80, 60, 'Seed Pokémon', 'There is a bud on this Pokémon’s back. To support its weight, Ivysaur’s legs and trunk grow thick and strong. If it starts spending more time lying in the sunlight, it’s a sign that the bud will bloom into a large flower soon.', '1 m', '13 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/002.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/002.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/002.png', 1),
(3, 'Venusaur', 80, 82, 83, 100, 100, 80, 'Seed Pokémon', 'There is a large flower on Venusaur’s back. The flower is said to take on vivid colors if it gets plenty of nutrition and sunlight. The flower’s aroma soothes the emotions of people.', '2 m', '100 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/003.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/003.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/003.png', 1),
(4, 'Charmander', 39, 52, 43, 60, 50, 65, 'Lizard Pokémon', 'The flame that burns at the tip of its tail is an indication of its emotions. The flame wavers when Charmander is enjoying itself. If the Pokémon becomes enraged, the flame burns fiercely.', '0.6 m', '8.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/004.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/004.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/004.png', 1),
(5, 'Charmeleon', 58, 64, 58, 80, 65, 80, 'Flame Pokémon', 'Charmeleon mercilessly destroys its foes using its sharp claws. If it encounters a strong foe, it turns aggressive. In this excited state, the flame at the tip of its tail flares with a bluish white color.', '1.1 m', '19 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/005.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/005.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/005.png', 1),
(6, 'Charizard', 78, 84, 78, 109, 85, 100, 'Flame Pokémon', 'Charizard flies around the sky in search of powerful opponents. It breathes fire of such great heat that it melts anything. However, it never turns its fiery breath on any opponent weaker than itself.', '1.7 m', '90.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/006.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/006.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/006.png', 1),
(7, 'Squirtle', 44, 48, 65, 50, 64, 43, 'Tiny Turtle Pokémon', 'Squirtle’s shell is not merely used for protection. The shell’s rounded shape and the grooves on its surface help minimize resistance in water, enabling this Pokémon to swim at high speeds.', '0.5 m', '9 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/007.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/007.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/007.png', 1),
(8, 'Wartortle', 59, 63, 80, 65, 80, 58, 'Turtle Pokémon', 'Its tail is large and covered with a rich, thick fur. The tail becomes increasingly deeper in color as Wartortle ages. The scratches on its shell are evidence of this Pokémon’s toughness as a battler.', '1 m', '22.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/008.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/008.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/008.png', 1),
(9, 'Blastoise', 79, 83, 100, 85, 105, 78, 'Shellfish Pokémon', 'Blastoise has water spouts that protrude from its shell. The water spouts are very accurate. They can shoot bullets of water with enough accuracy to strike empty cans from a distance of over 160 feet.', '1.6 m', '85.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/009.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/009.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/009.png', 1),
(10, 'Caterpie', 45, 30, 35, 20, 20, 45, 'Worm Pokémon', 'Its body is soft and weak. In nature, its perpetual fate is to be seen by others as food.', '0.3 m', '2.9 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/010.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/010.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/010.png', 1),
(11, 'Metapod', 50, 20, 55, 25, 25, 30, 'Cocoon Pokémon', 'Its hard shell doesn’t crack a bit even if Pikipek pecks at it, but it will tip over, spilling out its insides.', '0.7 m', '9.9 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/011.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/011.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/011.png', 1),
(12, 'Butterfree', 60, 45, 50, 90, 80, 70, 'Butterfly Pokémon', 'Nectar from pretty flowers is its favorite food. In fields of flowers, it has heated battles with Cutiefly for territory.', '1.1 m', '32 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/012.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/012.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/012.png', 1),
(13, 'Weedle', 40, 35, 30, 20, 20, 50, 'Hairy Bug Pokémon', 'Weedle has an extremely acute sense of smell. It is capable of distinguishing its favorite kinds of leaves from those it dislikes just by sniffing with its big red proboscis (nose).', '0.3 m', '3.2 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/013.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/013.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/013.png', 1),
(14, 'Kakuna', 45, 25, 50, 25, 25, 35, 'Cocoon Pokémon', 'Kakuna remains virtually immobile as it clings to a tree. However, on the inside, it is extremely busy as it prepares for its coming evolution. This is evident from how hot the shell becomes to the touch.', '0.6 m', '10 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/014.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/014.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/014.png', 1),
(15, 'Beedrill', 65, 90, 40, 45, 80, 75, 'Poison Bee Pokémon', 'Beedrill is extremely territorial. No one should ever approach its nest—this is for their own safety. If angered, they will attack in a furious swarm.', '1 m', '29.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/015.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/015.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/015.png', 1),
(16, 'Pidgey', 40, 45, 40, 35, 35, 56, 'Tiny Bird Pokémon', 'Pidgey has an extremely sharp sense of direction. It is capable of unerringly returning home to its nest, however far it may be removed from its familiar surroundings.', '0.3 m', '1.8 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/016.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/016.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/016.png', 1),
(17, 'Pidgeotto', 63, 60, 55, 50, 50, 71, 'Bird Pokémon', 'Pidgeotto claims a large area as its own territory. This Pokémon flies around, patrolling its living space. If its territory is violated, it shows no mercy in thoroughly punishing the foe with its sharp claws.', '1.1 m', '30 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/017.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/017.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/017.png', 1),
(18, 'Pidgeot', 83, 80, 75, 70, 70, 101, 'Bird Pokémon', 'This Pokémon has a dazzling plumage of beautifully glossy feathers. Many Trainers are captivated by the striking beauty of the feathers on its head, compelling them to choose Pidgeot as their Pokémon.', '1.5 m', '39.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/018.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/018.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/018.png', 1),
(19, 'Rattata', 30, 56, 35, 25, 35, 72, 'Mouse Pokémon', 'This Pokémon is common but hazardous. Its sharp incisors can easily cut right through hard wood.', '0.3 m', '3.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/019.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/019.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/019.png', 1),
(20, 'Raticate', 55, 81, 60, 50, 70, 97, 'Mouse Pokémon', 'Its whiskers are essential for maintaining its balance. No matter how friendly you are, it will get angry and bite you if you touch its whiskers.', '0.7 m', '18.5 kg', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/sprites/020.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/thumbnails/020.png', 'https://raw.githubusercontent.com/Purukitto/pokemon-data.json/master/images/pokedex/hires/020.png', 1),
